"use client";

import React, { useState } from 'react';
import { Calendar, MapPin, Clock, Users, ExternalLink, Share2, Heart } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

interface EventData {
  id: string;
  title: string;
  description: string;
  date: string;
  time: string;
  location: string;
  organizer: string;
  attendees: number;
  maxAttendees?: number;
  price: string;
  category: string;
  imageUrl: string;
  lumaUrl: string;
  tags: string[];
}

interface EventCardProps {
  event: EventData;
  variant?: 'default' | 'compact' | 'featured';
}

const EventCard: React.FC<EventCardProps> = ({ event, variant = 'default' }) => {
  const [isLiked, setIsLiked] = useState(false);
  const [isSharing, setIsSharing] = useState(false);

  const handleShare = async () => {
    setIsSharing(true);
    try {
      if (navigator.share) {
        await navigator.share({
          title: event.title,
          text: event.description,
          url: event.lumaUrl,
        });
      } else {
        await navigator.clipboard.writeText(event.lumaUrl);
        alert('Event link copied to clipboard!');
      }
    } catch (error) {
      console.error('Error sharing:', error);
    } finally {
      setIsSharing(false);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
    });
  };

  if (variant === 'compact') {
    return (
      <Card className="p-4 hover:shadow-lg transition-all duration-300 border-2 border-border bg-background">
        <div className="flex gap-4">
          <div className="flex-shrink-0">
            <img
              src={event.imageUrl}
              alt={event.title}
              className="w-16 h-16 rounded-lg object-cover border-2 border-border"
            />
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex items-start justify-between">
              <h3 className="font-bold text-lg text-foreground truncate">{event.title}</h3>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsLiked(!isLiked)}
                className="flex-shrink-0 ml-2"
              >
                <Heart className={`w-4 h-4 ${isLiked ? 'fill-red-500 text-red-500' : 'text-muted-foreground'}`} />
              </Button>
            </div>
            <div className="flex items-center gap-2 mt-1 text-sm text-muted-foreground">
              <Calendar className="w-4 h-4" />
              <span>{formatDate(event.date)}</span>
              <Clock className="w-4 h-4 ml-2" />
              <span>{event.time}</span>
            </div>
            <div className="flex items-center justify-between mt-2">
              <Badge variant="secondary" className="text-xs">
                {event.category}
              </Badge>
              <Button size="sm" asChild>
                <a href={event.lumaUrl} target="_blank" rel="noopener noreferrer">
                  View Event
                </a>
              </Button>
            </div>
          </div>
        </div>
      </Card>
    );
  }

  if (variant === 'featured') {
    return (
      <Card className="overflow-hidden border-2 border-border bg-background shadow-lg hover:shadow-xl transition-all duration-300">
        <div className="relative">
          <img
            src={event.imageUrl}
            alt={event.title}
            className="w-full h-64 object-cover"
          />
          <div className="absolute top-4 right-4 flex gap-2">
            <Button
              variant="secondary"
              size="sm"
              onClick={() => setIsLiked(!isLiked)}
              className="bg-background/80 backdrop-blur-sm"
            >
              <Heart className={`w-4 h-4 ${isLiked ? 'fill-red-500 text-red-500' : ''}`} />
            </Button>
            <Button
              variant="secondary"
              size="sm"
              onClick={handleShare}
              disabled={isSharing}
              className="bg-background/80 backdrop-blur-sm"
            >
              <Share2 className="w-4 h-4" />
            </Button>
          </div>
          <div className="absolute bottom-4 left-4">
            <Badge className="bg-primary text-primary-foreground font-bold">
              {event.price}
            </Badge>
          </div>
        </div>
        <div className="p-6">
          <div className="flex items-start justify-between mb-3">
            <h2 className="text-2xl font-bold text-foreground leading-tight">{event.title}</h2>
            <Badge variant="outline" className="ml-2 flex-shrink-0">
              {event.category}
            </Badge>
          </div>

          <p className="text-muted-foreground mb-4 line-clamp-2">{event.description}</p>

          <div className="space-y-3 mb-4">
            <div className="flex items-center gap-2 text-sm">
              <Calendar className="w-4 h-4 text-primary" />
              <span className="font-medium">{formatDate(event.date)}</span>
              <Clock className="w-4 h-4 text-primary ml-2" />
              <span>{event.time}</span>
            </div>

            <div className="flex items-center gap-2 text-sm">
              <MapPin className="w-4 h-4 text-primary" />
              <span>{event.location}</span>
            </div>

            <div className="flex items-center gap-2 text-sm">
              <Users className="w-4 h-4 text-primary" />
              <span>
                {event.attendees} attending
                {event.maxAttendees && ` • ${event.maxAttendees - event.attendees} spots left`}
              </span>
            </div>
          </div>

          <div className="flex flex-wrap gap-2 mb-4">
            {event.tags.map((tag, index) => (
              <Badge key={index} variant="secondary" className="text-xs">
                {tag}
              </Badge>
            ))}
          </div>

          <div className="flex gap-2">
            <Button className="flex-1" asChild>
              <a href={event.lumaUrl} target="_blank" rel="noopener noreferrer">
                <ExternalLink className="w-4 h-4 mr-2" />
                Register on Luma
              </a>
            </Button>
          </div>
        </div>
      </Card>
    );
  }

  return (
    <Card className="overflow-hidden border-2 border-border bg-background hover:shadow-lg transition-all duration-300">
      <div className="relative">
        <img
          src={event.imageUrl}
          alt={event.title}
          className="w-full h-48 object-cover"
        />
        <div className="absolute top-3 right-3 flex gap-2">
          <Button
            variant="secondary"
            size="sm"
            onClick={() => setIsLiked(!isLiked)}
            className="bg-background/80 backdrop-blur-sm"
          >
            <Heart className={`w-4 h-4 ${isLiked ? 'fill-red-500 text-red-500' : ''}`} />
          </Button>
          <Button
            variant="secondary"
            size="sm"
            onClick={handleShare}
            disabled={isSharing}
            className="bg-background/80 backdrop-blur-sm"
          >
            <Share2 className="w-4 h-4" />
          </Button>
        </div>
        <div className="absolute bottom-3 left-3">
          <Badge className="bg-primary text-primary-foreground font-bold">
            {event.price}
          </Badge>
        </div>
      </div>

      <div className="p-4">
        <div className="flex items-start justify-between mb-2">
          <h3 className="text-xl font-bold text-foreground leading-tight">{event.title}</h3>
          <Badge variant="outline" className="ml-2 flex-shrink-0">
            {event.category}
          </Badge>
        </div>

        <p className="text-muted-foreground text-sm mb-3 line-clamp-2">{event.description}</p>

        <div className="space-y-2 mb-3">
          <div className="flex items-center gap-2 text-sm">
            <Calendar className="w-4 h-4 text-primary" />
            <span>{formatDate(event.date)} at {event.time}</span>
          </div>

          <div className="flex items-center gap-2 text-sm">
            <MapPin className="w-4 h-4 text-primary" />
            <span className="truncate">{event.location}</span>
          </div>

          <div className="flex items-center gap-2 text-sm">
            <Users className="w-4 h-4 text-primary" />
            <span>{event.attendees} attending</span>
          </div>
        </div>

        <div className="flex flex-wrap gap-1 mb-3">
          {event.tags.slice(0, 3).map((tag, index) => (
            <Badge key={index} variant="secondary" className="text-xs">
              {tag}
            </Badge>
          ))}
          {event.tags.length > 3 && (
            <Badge variant="secondary" className="text-xs">
              +{event.tags.length - 3}
            </Badge>
          )}
        </div>

        <Button className="w-full" asChild>
          <a href={event.lumaUrl} target="_blank" rel="noopener noreferrer">
            <ExternalLink className="w-4 h-4 mr-2" />
            View on Luma
          </a>
        </Button>
      </div>
    </Card>
  );
};

interface EventsGridProps {
  events: EventData[];
  variant?: 'default' | 'compact' | 'featured';
  columns?: 1 | 2 | 3 | 4;
}

const EventsGrid: React.FC<EventsGridProps> = ({ 
  events, 
  variant = 'default',
  columns = 3 
}) => {
  const gridCols = {
    1: 'grid-cols-1',
    2: 'grid-cols-1 md:grid-cols-2',
    3: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3',
    4: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'
  };

  return (
    <div className={`grid gap-6 ${gridCols[columns]}`}>
      {events.map((event) => (
        <EventCard key={event.id} event={event} variant={variant} />
      ))}
    </div>
  );
};

const EventsSection: React.FC = () => {
  const sampleEvent: EventData = {
    id: '1',
    title: 'Tech Startup Networking Night',
    description: 'Join fellow entrepreneurs and tech enthusiasts for an evening of networking, insights, and collaboration. Perfect for founders, developers, and anyone passionate about innovation.',
    date: '2024-02-15',
    time: '6:00 PM',
    location: 'WeWork Downtown, San Francisco',
    organizer: 'SF Tech Community',
    attendees: 45,
    maxAttendees: 100,
    price: 'Free',
    category: 'Networking',
    imageUrl: 'https://images.unsplash.com/photo-1515187029135-18ee286d815b?w=500&h=300&fit=crop',
    lumaUrl: 'https://lu.ma/event/example-1',
    tags: ['Startups', 'Networking', 'Tech', 'Entrepreneurs']
  };

  return (
    <div className="min-h-screen bg-background p-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-foreground mb-4">
            Events
          </h1>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            Twice a week co-hosted sessions at different locations. Don't miss these amazing events to connect, learn, and grow with like-minded people.
          </p>
        </div>

        <div className="max-w-2xl mx-auto">
          <EventCard event={sampleEvent} variant="featured" />
        </div>
      </div>
    </div>
  );
};

export default EventsSection;
