import React, { useEffect, useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import CircularText from './CircularText';
import RotatingText from './RotatingText';
import './App.css';

function App() {
  const { scrollYProgress } = useScroll();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const unsubscribe = scrollYProgress.onChange((latest) => {
      if (latest > 0.3) {
        setIsVisible(true);
      }
    });

    return () => unsubscribe();
  }, [scrollYProgress]);

  const opacity = useTransform(scrollYProgress, [0.2, 0.4], [0, 1]);
  const scale = useTransform(scrollYProgress, [0.2, 0.4], [0.8, 1]);

  return (
    <div className="App">
      {/* First section - existing content */}
      <div className="container">
        <h1 className="main-title">Buildathon</h1>
        <div className="circular-text-overlay">
          <CircularText
            text=" GENZ 5-9 QUIT CLUB GENZ"
            onHover="speedUp"
            spinDuration={20}
            className="custom-class"
          />
        </div>
      </div>

      {/* Second section - scroll triggered */}
      <motion.div 
        className="scroll-section"
        style={{ opacity, scale }}
      >
        <div className="section-content">
          <h2 className="section-title">For</h2>
          <div className="rotating-text-container">
            <RotatingText
              texts={['developers','programmers','designers','visionaries']}
              mainClassName="px-2 sm:px-2 md:px-3 bg-cyan-300 text-black overflow-hidden py-0.5 sm:py-1 md:py-2 justify-center rounded-lg"
              staggerFrom="last"
              initial={{ y: "100%" }}
              animate={{ y: 0 }}
              exit={{ y: "-120%" }}
              staggerDuration={0.025}
              splitLevelClassName="overflow-hidden pb-0.5 sm:pb-1 md:pb-1"
              transition={{ type: "spring", damping: 30, stiffness: 400 }}
              rotationInterval={2000}
            />
          </div>
        </div>
      </motion.div>

      {/* Third section - building categories */}
      <motion.div 
        className="scroll-section building-section"
        initial={{ opacity: 0, y: 100 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
        viewport={{ once: true }}
      >
        <div className="section-content">
          <h2 className="section-title">Building</h2>
          <div className="rotating-text-container">
            <RotatingText
              texts={['crypto','AI agents','clothing line','media pod','health tech','SaaS']}
              mainClassName="px-2 sm:px-2 md:px-3 bg-purple-400 text-white overflow-hidden py-0.5 sm:py-1 md:py-2 justify-center rounded-lg"
              staggerFrom="center"
              initial={{ y: "100%" }}
              animate={{ y: 0 }}
              exit={{ y: "-120%" }}
              staggerDuration={0.03}
              splitLevelClassName="overflow-hidden pb-0.5 sm:pb-1 md:pb-1"
              transition={{ type: "spring", damping: 25, stiffness: 350 }}
              rotationInterval={2500}
            />
          </div>
        </div>
      </motion.div>
    </div>
  );
}

export default App;
